package org.app;

import java.util.HashMap;

public class FindTheDifference {
    public static Character findTheDifference(String s, String t){
        HashMap<Character, Integer> sMap = new HashMap<>();

        for(int i=0; i<s.length(); i++){
            if(sMap.containsKey(s.charAt(i))){
                sMap.put(s.charAt(i), sMap.get(s.charAt(i))+1);
            }
            else{
                sMap.put(s.charAt(i), 1);
            }
        }

        for(int i=0; i<t.length(); i++){
            if(sMap.containsKey(t.charAt(i))){
                sMap.put(t.charAt(i), sMap.get(t.charAt(i))-1);
            }
            else{
                return t.charAt(i);
            }
        }

        for(Character c : sMap.keySet()){
            if(sMap.get(c)<0){
                return c;
            }
        }

        throw new IllegalArgumentException("Strings are the same!");
    }

    public static void main(String[] args){
        System.out.println(findTheDifference("", ""));
    }

}
